function base64url(input) {
  return Buffer.from(input)
    .toString('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');
}

const [id, secret] = '684a0249352b2d0001c0354b:7ae3afec9719efe9fd1dea1538aa234860cecbbbdc9bb07ba7f9cee2c0b3d3e2'.split(':');
const header = { alg: 'HS256', typ: 'JWT', kid: id };
const payload = {
  iat: Math.floor(Date.now() / 1000),
  exp: Math.floor(Date.now() / 1000) + 5 * 60,
  aud: '/admin/'  // 👈 Fixed this line
};

const crypto = require('crypto');

const encodedHeader = base64url(JSON.stringify(header));
const encodedPayload = base64url(JSON.stringify(payload));
const token = `${encodedHeader}.${encodedPayload}`;

const hmac = crypto.createHmac('sha256', Buffer.from(secret, 'hex'));
hmac.update(token);
const signature = base64url(hmac.digest());

return [{ json: { token: `${token}.${signature}` } }];

